sys_script.script:
(function executeRule(current, previous /*null when async*/) {
gs.info("Create Client Profile Business Rule");
	try{
		var requireSshKey = requiresSshKey(current);
		var sftpClient = isSftpClient(current);

		if(requireSshKey && gs.nil(current.cg_key_id)){
			gs.debug("The current client profile requires ssh key authentication but the CG key id apears to be empty.");
		} else {
			var commProfiles = new CommProfiles();
			if(gs.nil(current.cg_client_profile_id)){
				gs.debug("create client profile is_sftp_client={0}, requires_ssh_key={1}, cg_key_id={2}, cg_client_profile_id={3}", 
				 sftpClient, requireSshKey, current.cg_key_id, current.cg_client_profile_id);

				commProfiles.sendAsyncClientProfileCreate(current, false);
			}
		}
	} catch (ex){
		gs.error(ex.message);
	}
})(current, previous);

function isSftpClient(current){
	return !gs.nil(current.sftp_client);
}

function requiresSshKey(current){
	return !gs.nil(current.sftp_client) && current.sftp_client.login_type == "PUBLIC_KEY";
}

function getFlowId(clientRecord){
	var flowRecord = new GlideRecord("x_305439_digital_m_flow_settings");
	var query = flowRecord.addQuery("client_profile_1", clientRecord.sys_id);
	query.addOrCondition("client_profile_2", clientRecord.sys_id);
	flowRecord.query();
	if(flowRecord.next()){
		return flowRecord.sys_id;
	} else {
		gs.error("Cannot find the flow settings to which this client profile belongs to.");
	}
}
