	removeDepenency(current.client_profile_1, "x_305439_digital_m_communication_links", "client_profile_1");
	removeDepenency(current.client_profile_2, "x_305439_digital_m_communication_links", "client_profile_2");
	removeDepenency(current.server_profile_1, "x_305439_digital_m_server_comm_profiles", "server_profile_1");
	removeDepenency(current.server_profile_2, "x_305439_digital_m_server_comm_profiles", "server_profile_2");
	removeDepenency(current.model_0023, "x_305439_digital_m_app_cft_pesit_push_st_sftp_pull_partner", "model_0023");
	removeDepenency(current.model_0043, "x_305439_digital_m_partner_sftp_push_st_sftp_pull_partner", "model_0043");

})(current, previous);

function removeDepenency(sys_id, table_name, field_name){
	var record = new GlideRecord(table_name);
	if(record.get(sys_id)){
		gs.info("deleting {0}.", field_name);
		record.deleteRecord();
	}
}
