sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
       return;
    }

	if (oldValue == newValue){
		return;
	}

	jslog("On Changed flow_name");

   //Type appropriate comment here, and begin script below

    var ga = new GlideAjax('FlowValidator');
	ga.addParam('sysparm_name','flowExists');
	ga.addParam('sysparm_flow_name', newValue);
	ga.getXMLAnswer(callback);
}

function callback(answer){
	jslog("FlowValidator result: " + answer);
	if(answer && answer === 'true'){
		g_scratchpad.validation_error = "true";
		g_scratchpad.validation_error_field = "flow_name";
		g_form.showFieldMsg("flow_name", "Invalid flow name. A flow with this name already exists.", "error", true);
	} else {
		g_form.hideFieldMsg("flow_name");
		g_scratchpad.validation_error = "false";
		g_scratchpad.validation_error_field = "";
	}
}
