sys_script_client.script:
function onLoad() {
   //Type appropriate comment here, and begin script below
   try {
	   if(!g_form.isNewRecord()){
		   var clientCertAliases = g_scratchpad.client_cert_aliases;
		   var clientKeyAliases = g_scratchpad.client_key_aliases;
		   var serverCertAliases = g_scratchpad.server_cert_aliases;

		   jslog("client_cert_aliases: " + g_scratchpad.client_cert_aliases);
		   jslog("client_key_aliases: " + g_scratchpad.client_key_aliases);
		   jslog("server_cert_aliases: " + g_scratchpad.server_cert_aliases);

		   var client_prefix;
		   var server_prefix;

		   if(g_user.hasRole("x_305439_digital_m.app_sender")){
			   client_prefix = "client_profile_1.";
			   server_prefix = "server_profile_1.";
		   } else {
			   client_prefix = "client_profile_2.";
			   server_prefix = "server_profile_2.";
		   }


		   if(clientCertAliases != undefined){
			   clientCertAliases.forEach(function(alias){
				   g_form.addOption(client_prefix + "pesit_client.certificate_alias", alias, alias);
			   });
		   }

		   if(clientKeyAliases != undefined){
			   clientKeyAliases.forEach(function(alias){
				   jslog("adding key " + alias + " to " + client_prefix + "sftp_client.key_alias");
				   g_form.addOption(client_prefix + "sftp_client.key_alias", alias, alias);
			   });
		   }

		   if(serverCertAliases != undefined){
			   serverCertAliases.forEach(function(alias){
				   g_form.addOption(server_prefix + "certificate_alias", alias, alias);
			   });
		   }
	   }
   } catch (error){
	   jslog("exception was thrown from loadAliases script");
	   jslog(error.message);
   }
}
