sys_script_client.script:
function onLoad() {
   //Type appropriate comment here, and begin script below
   try {
	   if(!g_form.isNewRecord()){
		   jslog("cert_aliases: " + g_scratchpad.cert_aliases);
		   jslog("server_cert_aliases: " + g_scratchpad.server_cert_aliases);
		   if(g_scratchpad.cert_aliases != undefined){
			   g_scratchpad.cert_aliases.forEach(function(alias){
			   g_form.addOption("client_profile_1.pesit_client.certificate_alias", alias, alias);
			   });
		   }

		   if(g_scratchpad.server_cert_aliases != undefined){
			   g_scratchpad.server_cert_aliases.forEach(function(alias){
			   g_form.addOption("server_profile_1.certificate_alias", alias, alias);
			   });
		   }
	   }
   } catch (error){
	   jslog("exception was thrown from loadAliases script");
	   jslog(error.message);
   }
}
