sys_script_client.script:
function onLoad() {
   //Type appropriate comment here, and begin script below
   try {
	   if(!g_form.isNewRecord()){
		   var clientCertAliases = g_scratchpad.client_cert_aliases;
		   var clientKeyAliases = g_scratchpad.client_key_aliases;
		   var serverCertAliases = g_scratchpad.server_cert_aliases;
		   var serverKeyAliases = g_scratchpad.server_key_aliases;

		   jslog("client_cert_aliases: " + clientCertAliases);
		   jslog("client_key_aliases: " + clientKeyAliases);
		   jslog("server_cert_aliases: " + serverCertAliases);
		   jslog("server_key_aliases: " + serverKeyAliases);

		   var client_prefix;
		   var server_prefix;

		   if(g_user.hasRole("x_305439_digital_m.app_sender")){
			   client_prefix = "client_profile_1.";
			   server_prefix = "server_profile_1.";
		   } else {
			   client_prefix = "client_profile_2.";
			   server_prefix = "server_profile_2.";
		   }

		   setAliases(clientCertAliases, client_prefix + "pesit_client.certificate_alias");
		   setAliases(clientKeyAliases, client_prefix + "sftp_client.key_alias");
		   setAliases(serverCertAliases, server_prefix + "certificate_alias");
		   setAliases(serverKeyAliases, server_prefix + "login_key_alias");

	   }
   } catch (error){
	   jslog("exception was thrown from loadAliases script");
	   jslog(error.message);
   }
}

function setAliases(aliases_array, alias_field_name){
	if(aliases_array != undefined){
		var alias_field = g_form.getValue(alias_field_name);
		aliases_array.forEach(function(alias){
			if(alias_field === "undefined" || alias_field === ""){
				g_form.setValue(alias_field_name, alias);
				alias_field = alias;
			}
			jslog("adding '" + alias + "' to " + alias_field_name);
			g_form.addOption(alias_field_name, alias, alias);

		});
	}
}
