sys_script_client.script:
function onLoad() {
	try {
		jslog("g_scratchpad.load_aliases: " + g_scratchpad.load_aliases);
		var view = getView();

		if(!g_form.isNewRecord() && g_scratchpad.load_aliases && (view == "" || view == "settings")){
			jslog("initiate load on aliases... ");
			loadAliases();
		}
	} catch (ex){
		jslog(ex.message);
	}
}

function loadAliases(){
	if(g_scratchpad.flow_model_name == "APP_CFT_PESIT_PUSH_ST_SFTP_PULL_PARTNER"){
		if(g_user.hasRole("x_305439_digital_m.app_sender")){
			getProductCertificates("client", "client_profile_1.pesit_client.certificate_alias");
			getProductCertificates("server", "server_profile_1.certificate_alias");
		} else {
			getLocalKeyAliases("client_profile_2.sftp_client.key_alias");
			getProductCertificates("server", "server_profile_2.login_key_alias");
		}
	} else {
		if(g_user.hasRole("x_305439_digital_m.app_sender")){
			getLocalKeyAliases("client_profile_1.sftp_client.key_alias");
			getProductKeys("server_profile_1.login_key_alias");
		} else {
			getLocalKeyAliases("client_profile_2.sftp_client.key_alias");
			getProductKeys("server_profile_2.login_key_alias");
		}
	}
}

function getProductCertificates(type, field_name){
	jslog("getProductCertificates type=" + type + ", field_name=" + field_name);
	var ga = new GlideAjax('LoadAliases');
	ga.addParam('sysparm_name','getAliases');
	ga.addParam('sysparm_alias_type', "product_certs");
	ga.addParam('sysparm_certs_type', type);
	ga.addParam("sysparm_current_id", g_form.getUniqueValue());
	ga.getXML(function(response){initAliases(response, field_name);});
}

function getLocalKeyAliases(field_name){
	jslog("getLocalKeyAliases field_name=" + field_name);
	var ga = new GlideAjax('LoadAliases');
	ga.addParam('sysparm_name','getAliases');
	ga.addParam('sysparm_alias_type', "local_keys");
	ga.addParam("sysparm_current_id", g_form.getUniqueValue());
	ga.getXML(function(response){initAliases(response, field_name);});
}

function getProductKeys(field_name){
	jslog("getProductKeys field_name=" + field_name);
	var ga = new GlideAjax('LoadAliases');
	ga.addParam('sysparm_name','getAliases');
	ga.addParam('sysparm_alias_type', "product_keys");
	ga.addParam("sysparm_current_id", g_form.getUniqueValue());
	ga.getXML(function(response){initAliases(response, field_name);});
}

function initAliases(response, alias_field_name){	
	var aliases = response.responseXML.documentElement.getElementsByTagName("alias");
	jslog("response for alias_field_name=" + alias_field_name + " aliases=" + aliases);
	var aliasesArray = [];
	for(var i = 0; i < aliases.length; i ++) { 
		var alias = aliases[i].getAttribute("name");
		aliasesArray.push(alias);
    }

	setAllAliases(aliasesArray, alias_field_name);
}

function setAllAliases(aliases_array, alias_field_name){
	if(aliases_array != undefined){
		var alias_field = g_form.getValue(alias_field_name);
		aliases_array.forEach(function(alias){
			if(alias_field === "undefined" || alias_field === ""){
				g_form.setValue(alias_field_name, alias);
				alias_field = alias;
			}
			jslog("adding '" + alias + "' to " + alias_field_name);
			g_form.addOption(alias_field_name, alias, alias);
		});
	}
}
