sys_script_client.script:
function onLoad() {
	try {
		if(!g_form.isNewRecord()){
			loadAliases();
		}
	} catch (ex){
		jslog(ex.message);
	}
}

function loadAliases(){

	if(g_scratchpad.flow_model_name == "APP_CFT_PESIT_PUSH_ST_SFTP_PULL_PARTNER"){
		if(g_user.hasRole("x_305439_digital_m.app_sender")){
			clientCertAliases = getProductCertificates("client");
		} else {
			clientKeyAliases = getLocalKeyAliases();
		}
		serverAliases = getProductCertificates("server");
	} else {
		serverKeyAliases = getProductKeys();
		clientKeyAliases = getLocalKeyAliases();
	}

}

function getProductCertificates(type){
	var ga = new GlideAjax('LoadAliases');
	ga.addParam('sysparm_name','getAliases');
	ga.addParam('sysparm_alias_type', "product_certs");
	ga.addParam('sysparm_certs_type', type);
	ga.addParam("sysparm_current_id", g_form.getUniqueValue());
	ga.getXMLAnswer(setAliases);
}

function getLocalKeyAliases(){
	var ga = new GlideAjax('LoadAliases');
	ga.addParam('sysparm_name','getAliases');
	ga.addParam('sysparm_alias_type', "local_keys");
	ga.addParam("sysparm_current_id", g_form.getUniqueValue());
	ga.getXMLAnswer(setAliases);
}

function getProductKeys(){
	var ga = new GlideAjax('LoadAliases');
	ga.addParam('sysparm_name','getAliases');
	ga.addParam('sysparm_alias_type', "product_keys");
	ga.addParam("sysparm_current_id", g_form.getUniqueValue());
	ga.getXMLAnswer(setAliases);
}

function setAliases(answer){
	jslog("Load aliases result: " + answer);

}
