sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	gs.info("refresh profiles triggered");

	try {
		var cgStRelayId = new GetStRelay().getStRelayId();
		var utils = new CommProfilesUtils();
		var cgClientId = utils.getClientBusinessId(current, gs.getUser());

		var cgCommLinksProfiles = utils.getCommunicationLinks(cgClientId, cgStRelayId, current.flow_model_name);
		var client_profile_field = utils.getClientProfileFieldSn(current);
		var linkId;

		if(cgCommLinksProfiles.length == 0 ){
			gs.info("There isn't any client communication links in CG.");
			if(!gs.nil(client_profile_field)){
				// clean up the already existing profile
				// client_profile_field.delete
			} else {
				// create empty client communication profile
// 				linkId = utils.createEmptyClientProfile(current.flow_model_name);
// 				current.setValue(client_profile_field.getName(), linkId);
// 				current.update();
			}
		} else {
			gs.info("Client communication profiles: " + JSON.stringify(cgCommLinksProfiles));
			if(gs.nil(client_profile_field)){
				// create
				linkId = utils.createNewClientCommunication(cgCommLinksProfiles[0].link.client.communication_profile_businessId,
															cgCommLinksProfiles[0].link.server.communication_profile_businessId,
															current.source,
															current.destination,
															cgCommLinksProfiles[0].pattern.pattern_data.name,
															cgCommLinksProfiles[0].link.client.communication_profile);
				current.setValue(client_profile_field.getName(), linkId);
				current.update();
			} else {
				// update
				utils.updateClientProfile(current,
										cgCommLinksProfiles[0].link.client.communication_profile_businessId,
										cgCommLinksProfiles[0].link.server.communication_profile_businessId,
										current.source,
										current.destination,
										cgCommLinksProfiles[0].pattern.pattern_data.name,
										cgCommLinksProfiles[0].link.client.communication_profile);
			}
		}
	}
	catch(ex) {
		var message = ex.message;
		gs.error(message);
	}

})(current, previous);
