sys_script.script:
(function executeRule(current, previous /*null when async*/) {
gs.info("System property changed...");

	if(gs.nil(gs.getProperty("x_305439_digital_m.amft.host"))){
		gs.addErrorMessage("amft.host cannot be empty.");
		current.setAbortAction(true);
		return;
	}

	if(gs.nil(gs.getProperty("x_305439_digital_m.amft.port"))){
		gs.addErrorMessage("amft.port cannot be empty.");
		current.setAbortAction(true);
		return;
	}

	if(gs.nil(gs.getProperty("x_305439_digital_m.http.timeout"))){
		gs.addErrorMessage("http.timeout cannot be empty.");
		current.setAbortAction(true);
		return;
	}

	try{
		var connHelper = new ConnectionSettings();
		var r = new sn_ws.RESTMessageV2('x_305439_digital_m.AMFT-Participants', 'Get ST relay');
		connHelper.setMandatoryProps(r);
		r.setStringParameterNoEscape('st_relay_name', current.value);

		var response = r.execute();
		var responseBody = response.getBody();
		var httpStatus = response.getStatusCode();
		if(httpStatus == 200){
			var jsonObj = JSON.parse(responseBody);
			if (jsonObj.data.length > 0){
				var id = jsonObj.data[0].businessId;
				var host = jsonObj.data[0].host;
				gs.setProperty("x_305439_digital_m.st.relay.id", id);
				gs.setProperty("x_305439_digital_m.st.relay.host", host);
				gs.info("x_305439_digital_m.st.relay.id=" + gs.getProperty("x_305439_digital_m.st.relay.id"));
				gs.info("x_305439_digital_m.st.relay.host=" + gs.getProperty("x_305439_digital_m.st.relay.host"));
			} else {
				current.setAbortAction(true);
				gs.addErrorMessage("Cannot find in CG product with name=" + current.value);
				gs.error("Cannot find in CG product with name=" + current.value);
			}
		} else {
			current.setAbortAction(true);
			gs.addErrorMessage("Failed to get SecureTransport product from CG. Http status is [" + httpStatus + "]");
			gs.error("Failed to get SecureTransport product from CG. Http status is [" + httpStatus + "]. Response body: " + responseBody);
		}
	} catch (ex){
		current.setAbortAction(true);
		gs.addErrorMessage("Failed to set st.relay.name");
		gs.error("Cannot update system property x_305439_digital_m.st.relay.id. Error: {0}", ex.message);
	}
})(current, previous);
