sys_script_include.script:
var ParticipantsUtil = Class.create();
ParticipantsUtil.prototype = {
    initialize: function() {
		this.expireTimeInMin = 30;
    },
	getParticipantsCount: function (participantType){
		var count = new GlideAggregate("x_305439_digital_m_participants");
		count.addAggregate('COUNT');
		count.addQuery('type', "=", participantType);
		count.query();
		var foundRecords = 0;
		if (count.next()) {
			foundRecords = count.getAggregate('COUNT');
		}
		gs.info("found participants records= " + foundRecords + ", type=" + participantType);
		return foundRecords;
	},
	getExpiredParticipants: function (participantType){
		var g_record = new GlideRecord("x_305439_digital_m_participants");
// 		g_record.addQuery('sys_updated_on', "<", gs.minutesAgo(this.expireTimeInMin));
		g_record.addQuery('type', "=", participantType);
		g_record.query();
		var partInfo = {};
		while(g_record.next()){
			partInfo[g_record.cg_id] = g_record.participant_name;
		}
		return partInfo;
	},
	plural: function (participantType){
		return participantType + "s";
	}, 
    type: 'ParticipantsUtil'
};
