var CreateFlow = Class.create();
CreateFlow.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	create: function (){
		var flowRecord = new GlideRecord("x_305439_digital_m_flow_settings");
		flowRecord.initialize();
		flowRecord.setValue("flow_name", this.getParameter("sysparm_flow_name"));
		flowRecord.setValue("destination", this.getParameter("sysparm_destination_name"));
		var id = flowRecord.insert();
		return id;
	},
    type: 'CreateFlow'
});
