sys_script_include.script:
var ConnectionSettings = Class.create();
ConnectionSettings.prototype = {
    initialize: function() {
    },

	/**
		Set mandatory settings - host, port and timeout for a RESTMessageV2 object.
		@{param} @type{RESTMessageV2} request
	*/
	setMandatoryProps: function (request){
		request.setHttpTimeout(gs.getProperty("x_305439_digital_m.http.timeout"));
		request.setStringParameterNoEscape('amft_host', gs.getProperty("x_305439_digital_m.amft.host"));
		request.setStringParameterNoEscape('amft_port', gs.getProperty("x_305439_digital_m.amft.port"));
	},

    type: 'ConnectionSettings'
};
