sys_script_include.script:
var FlowValidator = Class.create();
FlowValidator.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	flowExists: function (){
	    var flowName = this.getParameter('sysparm_flow_name');
		gs.debug("Check if a flow with name '" + flowName + "' already exists.");
		try{
			var name = this.getParameter('sysparm_flow_name');

			var record = new GlideRecord("x_305439_digital_m_flow_settings");
			record.addQuery("flow_name", name);
			record.query();
			var foundRecord = record.next();

			gs.debug("Is is flow with name={0} found? '{1}'", flowName, foundRecord);
			if(foundRecord){
				return true;
			}
			return false;
		} catch (ex){
			gs.error("Error while checking if flow exists. " + ex.message);
		}
		return true;
	},
    type: 'FlowValidator'
});
