sys_script_include.script:
var FlowClintHelper = Class.create();
FlowClintHelper.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	sendUpdate: function (){
		var sys_id = this.getParameter('sysparm_flow_id');
		try {
			gs.info("send update sys_id=" + sys_id);
			var flow = this._getFlowRecord(sys_id);
			gs.info("pesit login: " + flow.client_profile_1.pesit_client.login_name);
		} catch (ex){
			gs.error("failed to update. " + ex.message);
		}
	},

	_getFlowRecord: function (sys_id){
		var flow_record = new GlideRecord("x_305439_digital_m_flow_settings");
		if(flow_record.get(sys_id)){
			gs.info("the record is found");
			return flow_record;
		} else {
			gs.info("record is not found.");
		}
	},
    type: 'FlowClintHelper'
});
