function createClientProfile(){
	var utils = new CommProfilesUtils();
	var profile = utils.getClientProfileFieldSn(current, gs.getUser());
	if(gs.nil(profile.profile) || gs.nil(profile.profile.sys_created_on)){
		var linkId;
		if (current.flow_model_name == "APP_CFT_PESIT_PUSH_ST_SFTP_PULL_PARTNER" && gs.getUser().hasRole("x_305439_digital_m.app_sender")){
			linkId = utils.createEmptyClientProfile("PESIT");
		} else {
			linkId = utils.createEmptyClientProfile("SFTP");
		}
		current.setValue(profile.field_name, linkId);
	}
}

