sys_ui_action.script:
deploy();

function deploy(){
	try { 
		var r = new sn_ws.RESTMessageV2('x_305439_digital_m.AMFT-Flows', 'Deploy');
		r.setRequestBody(getRequestBody());

		var response = r.execute();
		var responseBody = response.getBody();
		var httpStatus = response.getStatusCode();
		gs.info("deploy http status: " + httpStatus);
		action.setRedirectURL(current);
		if(httpStatus == 202){
			gs.addInfoMessage("Flow with name '" + current.flow_name + "' request has been accepted for processing, but the processing has not been completed.");
		} else if (httpStatus == 400){
			gs.error("Business ids is empty or invalid.");
		} else if (httpStatus == 412){
			gs.addErrorMessage("The deploy request has been triggered, but some products require restart or generates conflict with legacy flows.");
		} else {
			gs.addErrorMessage("General error. Deployment procedure has failed.");
		}

	} catch(ex) {
		 var message = ex.message;
	}
}

function getRequestBody(){
	var flowRequest = [];
	flowRequest.push(current.cg_id);
	return JSON.stringify(flowRequest);
}
