function sendRequest(){
	try {
		gs.info("sending flow create request to CG.");
		var r = new sn_ws.RESTMessageV2('x_305439_digital_m.AMFT-Flows', 'Create a Flow');
		r.setStringParameterNoEscape('flow_name', current.flow_model_name);
		r.setRequestBody(getRequestBody());

		var response = r.execute();
		var responseBody = response.getBody();
		var httpStatus = response.getStatusCode();

		gs.info("http status: " + httpStatus);

		var responseBodyObj = JSON.parse(responseBody);
		if(httpStatus != 201){
			action.setRedirectURL(current);
			gs.addErrorMessage(responseBodyObj.errors);
		} else {
			action.setRedirectURL(current);
			gs.info("responseBodyObj.data.statusFlow: " + responseBodyObj.data.statusFlow);
			current.setValue("cg_status", responseBodyObj.data.statusFlow);

			current.update();
			gs.addInfoMessage("Flow with name '" + current.flow_name + "' successfully created.");
		}
	}
	catch(ex) {
		var message = ex.message;
		gs.error(message);
	}
}

function getRequestBody(){
	var flowRequest = {};
	flowRequest.flow_name = current.flow_name + "";
	flowRequest.flow_description = current.u_comments + "";
	flowRequest.flow_tags = ["servicenow"] + "";
	flowRequest.source_partner_id = current.source.cg_id + "";
	flowRequest.target_partner_id = current.destination.cg_id + "";
	return JSON.stringify(flowRequest);
}
