sys_ui_action.script:
function test_dmft(){

	var view = getView();
	console.log("current view: " + view);
	if(view === 'destsettings'){
		console.log("flow name: " + g_form.getValue('flow_name'));
		var gAjax = new GlideAjax("CreateFlow");
		gAjax.addParam('sysparm_name', "create");
		gAjax.addParam("sysparm_flow_name", g_form.getValue('flow_name'));
		gAjax.addParam("sysparm_destination_name", g_form.getValue('destination'));
		gAjax.getXML(createFlowCallBack);

	} else if (view === "sourcesettings"){
		console.log("flow source: " + g_form.getValue('source'));
		var id = g_form.getUniqueValue();
		g_form.save();
		switchToView('preview', id);
	}
}

function createFlowCallBack(response){
	var id = response.responseXML.documentElement.getAttribute("answer");
	if(id != null) {
		switchToView('sourcesettings', id);
	}
}

function switchToView(viewName, recordId){
	var gu = new GlideURL('x_305439_digital_m_flow_settings.do');
		var url = gu.addParam('sys_id', recordId);
		url = gu.addParam('sysparm_view', viewName);
		g_navigation.open(url.getURL()); 
}
