sys_ui_action.script:
function updateRecord(){
	jslog("client save action.");

	try{
		var isValidField = validateFields();
		var isRequiredFieldValid = checkRequiredFields();
		if(isValidField && isRequiredFieldValid){
			gsftSubmit(null, g_form.getFormElement(),'sysverb_update');
		}
	} catch (ex){
		jslog("Failure saving client settings. " + ex.message + " " + ex);
	}
}

function sendRequest(requestName){
	jslog("sending " + requestName);
	var ga = new GlideAjax('FlowClintHelper');
	ga.addParam('sysparm_name', requestName);
	ga.addParam('sysparm_flow_id', g_form.getUniqueValue());
	ga.getXML(callback);
}

function callback(){
	jslog("handled response");
}

function validateFields(){
	var field_name;
	if(g_scratchpad.flow_model_name == "APP_CFT_PESIT_PUSH_ST_SFTP_PULL_PARTNER"){
		field_name = "model_0023.destination_folder";
	} else {
		field_name = "model_0043.destination_folder";
	}

	var folder = g_form.getValue(field_name);
	if(folder !== undefined && folder !== null && folder !== "" && !folder.startsWith("/")){
		g_form.showFieldMsg(field_name,'Invalid folder','error',true);
		return false;
	}

	return true;
}

function checkRequiredFields(){
	if(g_scratchpad.flow_model_name == "APP_CFT_PESIT_PUSH_ST_SFTP_PULL_PARTNER"){
		if(isNullOrEmpty(g_form.getValue("model_0023.pesit_flow_idf"))){
			g_form.addErrorMessage("Flow IDF is required");
			return false;
		}

		if(isNullOrEmpty(g_form.getValue("model_0023.destination_file"))){
			g_form.addErrorMessage("Destination file is required.");
			return false;
		}

		if(isNullOrEmpty(g_form.getValue("model_0023.destination_folder"))){
			g_form.addErrorMessage("Destination folder is required.");
			return false;
		}
	}
	return true;
}

function isNullOrEmpty(field){
	return field === undefined || field === null || field === "";
}
