sys_ui_action.script:
try{
	current.update();

	var scope = gs.getCurrentScopeName();
	var table_prefix = current.getTableName();

	var flowHelper = new FlowUtils();
	var comProfiles = new CommProfiles();
	var comProfilesUtils = new CommProfilesUtils();

	var server_record = comProfilesUtils.getServerProfileFieldSn(current).profile;
	var client_record = comProfilesUtils.getClientProfileFieldSn(current).profile;
	var app_name = flowHelper.getClientAppName(current);
	var isPesit = flowHelper.isPesit(current);

	if(!isPesit){
		comProfiles.sendAsyncSshKeyUpload(current);
	}

	comProfiles.sendAsyncServerProfileCreate(server_record, app_name, isPesit, client_record.sys_id);
// 	flowHelper.sendAsyncFlowCreateRequest(current);

} catch (ex){
	gs.error("Finish action: " + ex.message);
}

