function source_settings(/*String*/ action_verb, /*String*/ sys_id, /*String*/ table, /*String*/ displayValue){
	this.destroy();
	var createNew = new GlideModalForm('Select Source', table);
	createNew.setSysID(sys_id);
	createNew.addParm('sysparm_view', 'sourcesettings');  // select the view
	createNew.addParm('sysparm_form_only', 'true');
	createNew.setOnloadCallback(function (iframeDoc){
		iframeDoc.$window[0].getElementsByClassName("modal-frame")[0].setAttribute("style", "width: 100%; height: 100%");  // try to expand the window on max
	});
	createNew.setCompletionCallback(preview);
	createNew.render();
}

function preview(/*String*/ action_verb, /*String*/ sys_id, /*String*/ table, /*String*/ displayValue){
	this.destroy();
	var createNew = new GlideModalForm('Flow preview', table);
	createNew.setSysID(sys_id);
	createNew.addParm('sysparm_view', 'preview');  // select the view
	createNew.addParm('sysparm_form_only', 'true');
	createNew.setOnloadCallback(function (iframeDoc){
		iframeDoc.$window[0].getElementsByClassName("modal-frame")[0].setAttribute("style", "width: 100%; height: 100%");  // try to expand the window on max
	});
	createNew.render();
}
