sys_ui_policy.script_true:
function onCondition() {
	jslog("PARTNER_SFTP_PUSH_ST_SFTP_PULL_PARTNER");
	jslog("has role app_sender: " + g_user.hasRole('x_305439_digital_m.app_sender'));
	jslog("has role app_receiver: " + g_user.hasRole('x_305439_digital_m.app_receiver'));
	if(g_user.hasRole('x_305439_digital_m.app_sender')){
		// show sftp client 1
		g_form.setDisplay("client_profile_1.sftp_client.login_name", true);
		g_form.setDisplay("client_profile_1.sftp_client.login_type", true);
		g_form.setDisplay("client_profile_1.sftp_client.password", true);
		g_form.setDisplay("client_profile_1.sftp_client.ssh_key", true);
		g_form.setDisplay("client_profile_1.sftp_client.key_alias", true);

		// hide pesit client 1
		g_form.setDisplay("client_profile_1.pesit_client.login_name", false);
		g_form.setDisplay("client_profile_1.pesit_client.certificate_alias", false);

		// hide sftp and pesit client 2
		g_form.setDisplay("client_profile_2.sftp_client.login_name", false);
		g_form.setDisplay("client_profile_2.sftp_client.login_type", false);
		g_form.setDisplay("client_profile_2.sftp_client.password", false);
		g_form.setDisplay("client_profile_2.sftp_client.ssh_key", false);
		g_form.setDisplay("client_profile_2.sftp_client.key_alias", false);
		g_form.setDisplay("client_profile_2.pesit_client.certificate_alias", false);
		g_form.setDisplay("client_profile_2.pesit_client.login_name", false);

		// hide sftp settings
		g_form.setDisplay("model_0023.destination_file", false);
		g_form.setDisplay("model_0023.destination_folder", false);
		g_form.setDisplay("model_0023.file_record_format", false);
		g_form.setDisplay("model_0023.maximum_record_length", false);
		g_form.setDisplay("model_0023.pesit_flow_idf", false);
		g_form.setDisplay("model_0023.relay_receive_file_as", false);
		g_form.setDisplay("model_0023.sftp_transfer_mode", false);
		g_form.setDisplay("model_0023.source_file_type", false);
		g_form.setDisplay("model_0023.use_default_max_record_length", false);

		// show server 1
		g_form.setDisplay("server_profile_1.host", true);
		g_form.setDisplay("server_profile_1.port", true);
		g_form.setDisplay("server_profile_1.certificate_alias", false);
		g_form.setDisplay("server_profile_1.login_key_alias", true);

		// hide server 2
		g_form.setDisplay("server_profile_2.host", false);
		g_form.setDisplay("server_profile_2.port", false);
		g_form.setDisplay("server_profile_2.certificate_alias", false);
		g_form.setDisplay("server_profile_2.login_key_alias", false);

		// readonly properties for the sender
		g_form.setReadOnly("model_0043.destination_file", true);
		g_form.setReadOnly("model_0043.destination_folder", true);
		g_form.setReadOnly("model_0043.src_transfer_mode", true);
		g_form.setReadOnly("model_0043.target_transfer_mode", true); 
		g_form.setReadOnly("flow_name", true); 
		g_form.setReadOnly("u_comments", true); 
		g_form.setReadOnly("destination", true); 
		g_form.setReadOnly("source", true);
		g_form.setReadOnly("relay", true);

	} else if (g_user.hasRole('x_305439_digital_m.app_receiver')){
		// hide client 1
		g_form.setDisplay("client_profile_1.sftp_client.login_name", false);
		g_form.setDisplay("client_profile_1.sftp_client.login_type", false);
		g_form.setDisplay("client_profile_1.sftp_client.password", false);
		g_form.setDisplay("client_profile_1.sftp_client.ssh_key", false);
		g_form.setDisplay("client_profile_1.sftp_client.key_alias", false);
		g_form.setDisplay("client_profile_1.pesit_client.login_name", false);
		g_form.setDisplay("client_profile_1.pesit_client.certificate_alias", false);

		// show sftp client 2
		g_form.setDisplay("client_profile_2.sftp_client.login_name", true);
		g_form.setDisplay("client_profile_2.sftp_client.login_type", true);
		g_form.setDisplay("client_profile_2.sftp_client.password", true);
		g_form.setDisplay("client_profile_2.sftp_client.ssh_key", false);
		g_form.setDisplay("client_profile_2.sftp_client.key_alias", true);

		// hide pesit client 2
		g_form.setDisplay("client_profile_2.pesit_client.certificate_alias", false);
		g_form.setDisplay("client_profile_2.pesit_client.login_name", false);

		// hide pesit settings
		g_form.setDisplay("model_0023.destination_file", false);
		g_form.setDisplay("model_0023.destination_folder", false);
		g_form.setDisplay("model_0023.file_record_format", false);
		g_form.setDisplay("model_0023.maximum_record_length", false);
		g_form.setDisplay("model_0023.pesit_flow_idf", false);
		g_form.setDisplay("model_0023.relay_receive_file_as", false);
		g_form.setDisplay("model_0023.sftp_transfer_mode", false);
		g_form.setDisplay("model_0023.source_file_type", false);
		g_form.setDisplay("model_0023.use_default_max_record_length", false);

		// show server 2
		g_form.setDisplay("server_profile_2.host", true);
		g_form.setDisplay("server_profile_2.port", true);
		g_form.setDisplay("server_profile_2.certificate_alias", false);
		g_form.setDisplay("server_profile_2.login_key_alias", true);

		// hide server 1
		g_form.setDisplay("server_profile_1.host", false);
		g_form.setDisplay("server_profile_1.port", false);
		g_form.setDisplay("server_profile_1.certificate_alias", false);
		g_form.setDisplay("server_profile_1.login_key_alias", false);
	}
}
