sys_ui_policy.script_true:
function onCondition() {
	jslog("OnLoad Client SFTP login type | flow_model_name = " + g_scratchpad.flow_model_name);
	try {
		if(g_user.hasRole("x_305439_digital_m.app_sender")){
			if(g_scratchpad.flow_model_name == "PARTNER_SFTP_PUSH_ST_SFTP_PULL_PARTNER"){
				jslog("onLoad sender 0043 " + g_form.getValue("client_profile_1.sftp_client.login_type"));
				if(g_form.getValue("client_profile_1.sftp_client.login_type") == 'PUBLIC_KEY' ){
					g_form.setDisabled("client_profile_1.sftp_client.password", true);
					g_form.setDisabled("client_profile_1.sftp_client.key_alias", false);
					toggleSSHKeyBtn(false);
				} else {
					g_form.setDisabled("client_profile_1.sftp_client.password", false);
					g_form.setDisabled("client_profile_1.sftp_client.key_alias", true);
					toggleSSHKeyBtn(true);
				}
			} 
		} else {
			jslog("onLoad receiver " + g_form.getValue("client_profile_2.sftp_client.login_type"));
			if(g_form.getValue("client_profile_2.sftp_client.login_type") == 'PUBLIC_KEY' ){
				g_form.setDisabled("client_profile_2.sftp_client.password", false);
				g_form.setDisabled("client_profile_2.sftp_client.key_alias", true);
				toggleSSHKeyBtn(true);
			} else {
				g_form.setDisabled("client_profile_2.sftp_client.password", true);
				g_form.setDisabled("client_profile_2.sftp_client.key_alias", false);
				toggleSSHKeyBtn(false);
			}
		}
	} catch (ex){
		jslog(ex.message);
	}
}
