sys_ui_policy.script_true:
function onCondition() {
	jslog("on load fired for 'OnLoad Client 1 SFTP login type'");
	jslog("OnLoad Client 1 SFTP login type| flow_model_name = " + g_scratchpad.flow_model_name);
	if(g_user.hasRole("x_305439_digital_m.app_sender")){
		if(g_scratchpad.flow_model_name == "PARTNER_SFTP_PUSH_ST_SFTP_PULL_PARTNER"){
			if(g_form.getValue("client_profile_1.sftp_client.login_type") == 'PUBLIC_KEY' ){
				g_form.setDisplay("client_profile_1.sftp_client.password", false);
				g_form.setDisplay("ssh_key", true);
			} else {
				g_form.setDisplay("client_profile_1.sftp_client.password", true);
				g_form.setDisplay("ssh_key", false);
			}
		} 
	} else {
		if(g_form.getValue("client_profile_2.sftp_client.login_type") == 'PUBLIC_KEY' ){
			g_form.setDisplay("client_profile_2.sftp_client.password", false);
			g_form.setDisplay("ssh_key", true);
		} else {
			g_form.setDisplay("client_profile_2.sftp_client.password", true);
			g_form.setDisplay("ssh_key", false);
		}
	}
}
